	/**
	 * Transcode a SVG image file into a PNG image file.
	 * In some environment (within gradle) I have seen : Invalid memory access of location 0x0 rip=0x10109e7a7 Segmentation fault
	 * It looks like this is related to an OutOfMemory exception combined with a JVM bug (Applke implementation)
	 * @param taxonId
	 * @param svgFile
	 * @return success or not
	 */
	public boolean SVGtoPNG(int taxonId, File svgFile){

		//Create a PNG transcoder since it doesn't seem Thread safe
		PNGTranscoder PNGTranscoder = new PNGTranscoder();
       
       	// Create the transcoder input.
        TranscoderInput input = new TranscoderInput(svgFile.toURI().toString());
        
		try {
			 // Create the transcoder output.
			OutputStream ostream = new FileOutputStream(config.getImageFolder() + config.getDistributionImagePrefix() + taxonId + PNG_FILE_EXT);
			TranscoderOutput output = new TranscoderOutput(ostream);
			
			PNGTranscoder.transcode(input, output);
	        ostream.flush();
	        ostream.close();
		} catch (FileNotFoundException e) {
			LOGGER.fatal("Could not write PNG distribution image for taxon " + taxonId, e);
			return false;
		}
		catch (TranscoderException e) {
			LOGGER.fatal("Could not write PNG distribution image for taxon " + taxonId, e);
			return false;
		} 
        catch (IOException e) {
        	LOGGER.fatal("Could not write PNG distribution image for taxon " + taxonId, e);
        	return false;
		}
		return true;
	}
	
	/**
	 * 
	 * @param distributionId id matching the net.canadensys.dataportal.vascan.constant.Distribution constant.
	 * @return
	 */
	private String getDistributionColor(int distributionId){
		switch(distributionId){
			case Distribution.ABSENT: return COLOR_DISTRIBUTION_ABSENT;
			case Distribution.INTRODUCED: return COLOR_DISTRIBUTION_INTRODUCED;
			case Distribution.NATIVE: return COLOR_DISTRIBUTION_NATIVE;
			case Distribution.EXTIRPATED: return COLOR_DISTRIBUTION_EXTIRPATED;
			case Distribution.EXCLUDED: return COLOR_DISTRIBUTION_EXCLUDED;
			case Distribution.DOUBTFUL: return COLOR_DISTRIBUTION_DOUBTFUL;
			case Distribution.EPHEMERE: return COLOR_DISTRIBUTION_EPHEMERAL;
			default: return COLOR_DISTRIBUTION_DEFAULT;
		}
	}
	
	/**
	 * 
	 * @param distributionLabel label matching the net.canadensys.dataportal.vascan.constant.Distribution constant.
	 * @return
	 */
	private String getDistributionColor(String distributionLabel){
		if(distributionLabel.equals(Distribution.ABSENT_LABEL)){
			return COLOR_DISTRIBUTION_ABSENT;
		}
		else if(distributionLabel.equals(Distribution.INTRODUCED_LABEL)){
			return COLOR_DISTRIBUTION_INTRODUCED;
		}
		else if(distributionLabel.equals(Distribution.NATIVE_LABEL)){
			return COLOR_DISTRIBUTION_NATIVE;
		}
		else if(distributionLabel.equals(Distribution.EXTIRPATED_LABEL)){
			return COLOR_DISTRIBUTION_EXTIRPATED;
		}
		else if(distributionLabel.equals(Distribution.EXCLUDED_LABEL)){
			return COLOR_DISTRIBUTION_EXCLUDED;
		}
		else if(distributionLabel.equals(Distribution.DOUBTFUL_LABEL)){
			return COLOR_DISTRIBUTION_DOUBTFUL;
		}
		else if(distributionLabel.equals(Distribution.EPHEMERAL_LABEL)){
			return COLOR_DISTRIBUTION_EPHEMERAL;
		}
		return COLOR_DISTRIBUTION_DEFAULT;
	}

